! function (e, t, o) {
    "use strict";
    ! function () {
        var PageTable = o("#TableAdmin").bootstrapTable({
            url: '/Attendance/GetResourceList',         //请求后台的URL（*）
            method: 'get',
            //search: !0,
            pagination: !0,
            showRefresh: !0,
            showColumns: !0,
            iconSize: "outline",
            clickToSelect: true,
            showColumns: true,
            toolbar: "#exampleTableEventsToolbar",
            pagination: true,
            sidePagination: "server",
            pageSize: 15,                       //每页的记录行数（*）
            pageList: [15, 25, 50, 100],        //可供选择的每页的行数（*）
        });
    }()

    //删除信息
    $("#btn_Del").click(function () {
        PostData(TableAdmin, "/Attendance/DelResourceList", o, "#TableAdmin");
    });

    //编辑信息
    $("#btn_Edit").click(function () {

        var selects = $(TableAdmin).bootstrapTable('getSelections');

        if (selects.length !== 1) {
            ToastShow("操作提示", "选择信息不符合要求", 3000, "必须选中一条数据", "warning");
            return;
        }

        var json = eval('(' + JSON.stringify(selects) + ')');

        //iframe窗 
        layer.open({
            type: 2,
            title: '编辑信息',
            shadeClose: true,
            shade: 0.8,
            area: ['100%', '100%'],
            content: "/Attendance/UpdateResource/" + json[0].ResourcesCode + '.html' //iframe的url
        });
    });


    //新增信息
    $("#btn_Add").click(function () {
        //iframe窗 
        layer.open({
            type: 2,
            title: '用户添加',
            shadeClose: true,
            shade: 0.8,
            area: ['100%', '100%'],
            content: "/Attendance/UpdateResource" //iframe的url
        });
    });



}(document, window, jQuery);
